import React, { useEffect, useRef, filterRef } from 'react';
import './styles.css'; // Global Styling
import './BeatingHeart.css'; 
import { startAuroraAnimation } from './AuroraAnimation';
import {motion, AnimatePresence} from "framer-motion";
import logo from './trinity.svg';



const BeatingHeart = () => {


    return (
        <motion.svg
          width="200"
          height="200"
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 24 24"
          animate={{ scale: [1, 1.2, 1] }} // Scale animation
          transition={{ duration: 1.5, repeat: Infinity }} // Repeat animation indefinitely
        >
          <circle cx="12" cy="12" r="10" fill="white" />
        </motion.svg>
      );
};

export default BeatingHeart;
