import './App.css';

import './styles.css'; // Global Styling
import LoadingScreen from './LoadingScreen';
import Canvas from './Canvas';
import NavBar from './NavBar';
import Aurora from './Aurora';
import {motion, AnimatePresence} from "framer-motion";
import React, { useState } from 'react';
import BeatingHeart from './BeatingHeart';
import flower from './assets/flower.svg';
import FlowerOfLife from './FlowerOfLife';


function App() {

  return (
    <div className="App">
      <NavBar/>
      <LoadingScreen/>
      <header className="App-header">
         <motion.div 
         initial={{ opacity: 0 }} // Initial opacity and scale of the image
         animate={{ opacity: 1}} // Fade in and scale in animation
         transition={{ delay: 1.6, duration: 1 }} // Duration of the animation
          className="App-container">
            <motion.div
              initial ={{ opacity: 0 }}
              animate={{ opacity: 1, y: -33}}
              transition={{ delay: 2, duration: 3 }}>
            <h1>Evoking Divinity To Amplify Human Consciousness.</h1>
          </motion.div>
          <motion.div
              initial ={{ opacity: 0 }}
              animate={{ opacity: 1, y: -33}}
              transition={{ delay: 3, duration: 3 }}>
            <h2>Los Angeles, California.</h2>
          </motion.div>
          <motion.div
              initial ={{ opacity: 0 }}
              animate={{ opacity: 1, y: -33}}
              transition={{ delay: 3, duration: 3 }}>
            <BeatingHeart/>
          </motion.div>
          <FlowerOfLife/>
        </motion.div>     
      <Canvas/>
      <Aurora/>

      </header>     
    </div>
  );
}

export default App;
