import React, { useEffect, useState } from 'react';
import './styles.css'; // Global Styling
import './Aurora.css';
import { startAuroraAnimation } from './AuroraAnimation';
import { AnimatePresence } from "framer-motion";

function Aurora() {

  const [isVisible, setIsVisible] = useState(false); // Set initial visibility to false

  useEffect(() => {

    const timeout = setTimeout(() => {
      setIsVisible(true); // Update visibility to true after 3 seconds
    }, 3000);
    startAuroraAnimation();

    return () => clearTimeout(timeout);
  }, []);

  return (
    <AnimatePresence>
      {isVisible && (
        <motion.div 
        initial={{ opacity: 0 }} // Initial opacity and scale of the image
        animate={{ opacity: 1}} // Fade in and scale in animation
        transition={{ delay: 1.2, duration: 1 }} // Duration of the animation
        className="aurora">
          <div className="g-aurora"></div>
          <div className="g-aurora"></div>
          <svg id='blob' version='1.1' xmlns='http://www.w3.org/2000/svg'>
            <defs>
              <filter id='wave'>
                <feTurbulence baseFrequency='0.00510 0.01099' id='turbulence' numOctaves='3' result='noise' seed='10' />
                <feDisplacementMap id='displacement' in2='noise' in='SourceGraphic' scale='96' />
              </filter>
            </defs>
          </svg>
        </motion.div>
      )}
    </AnimatePresence>
  );
};

export default Aurora;
