import React, { useEffect, useRef, filterRef } from 'react';
import './styles.css'; // Global Styling
import './Aurora.css'; 
import { startAuroraAnimation } from './AuroraAnimation';
import {motion, AnimatePresence} from "framer-motion";



function Aurora() {

  const [isVisible, setIsVisible] = useState(true);

  useEffect(() => {
      startAuroraAnimation();
  }, []);

  useEffect(() => {
    const timeout = setTimeout(() => {
      setIsVisible(false);
    }, 3000);

    // Clear the timeout when the component unmounts or when isVisible becomes true again
    return () => clearTimeout(timeout);
  }, []); // This effect runs only once after the component mounts

  return (
      isVisible &&
      <div className="aurora">
        <div className="g-aurora"></div>
        <div className="g-aurora"></div>
        <svg id='blob' version='1.1' xmlns='http://www.w3.org/2000/svg'>
          <defs>
            <filter id='wave' ref={filterRef}>
              <feTurbulence baseFrequency='0.00510 0.01099' id='turbulence' numOctaves='3' result='noise' seed='10' />
              <feDisplacementMap id='displacement' in2='noise' in='SourceGraphic' scale='96' />
            </filter>
          </defs>
        </svg>
      </div>
  );
};

export default Aurora;
